import React from 'react';

const Sidebar = (props) => {
  const {imageUploadHandler, handleImageUpload, handleMosaicView, handleSortPhotos, isSortModeOn} = props;
  return (
    <div className="sidebar-wrapper">
      <label className="btn primary-btn input-file-button" disabled={!isSortModeOn}>
        <input type="file" onChange={handleImageUpload}/>
        UPLOAD PHOTO
      </label>
      <button id="mosaic-btn" className="btn secondary-btn mosaic-view-btn" disabled={!isSortModeOn} onClick={handleMosaicView}>
        MOSAIC VIEW
      </button>
      <button id="sort-btn" className="btn" onClick={handleSortPhotos}>
        SORT IMAGES
      </button>
    </div>
  )
}

export default Sidebar;
