import React, {useState} from 'react';
import Cropper from 'react-easy-crop';

const PopUp = ({photoId, photos, setPhotos, togglePopUp}) => {
  let imageObj = photos.filter( photo => photo.id === photoId );
  //const getImage = <img src={image[0].url} alt={photoId} />;
  const [crop, setCrop] = useState({ x: 0, y: 0});
  const [zoom, setZoom] = useState(1)

  const onCropComplete = (croppedArea, croppedAreaPixels) => {
    console.log('test');
    let newCoordinates = [...crop];

    console.log('cropped area', croppedArea);
    console.log('croppedArea pixels', croppedAreaPixels);
  };


  const handleRemove = () => {
    let newPhotos = photos.filter( photo => photo.id !== photoId );
    setPhotos(newPhotos);
    togglePopUp('');
  }


  return (
    <div>
      <div className="popup-overlay" onClick={()=> togglePopUp('')}>
      </div>
      <div className="full-image-component">
        <div className="close-btn" onClick={()=> togglePopUp('')}>x</div>

        <div className="image-fullsize-container">
          <Cropper
          image={imageObj[0].url}
          crop={crop}
          zoom={zoom}
          aspect={1/1}
          onCropComplete={onCropComplete}
          onZoomChange={setZoom}
        />
      </div>
        <div className="image-actions">
          <button className="btn primary-btn crop-btn">Crop</button>
          <button className="danger-btn remove btn" onClick={handleRemove}>Remove</button>
        </div>
      </div>
    </div>
  );
}

export default PopUp;
