import React from 'react';
import ImageComponent from './ImageComponent';
import {Droppable} from 'react-beautiful-dnd';

const Dashboard = ({photos, handlePhotoOnClick}) => {
  let noImagesDiv = <div className="no-photos"> Step up your photography game. Upload your first photo :) </div>;
  return (
    <div className="dashboard-wrapper" id="dashboard">
      <Droppable droppableId='dashboard'>
        {provided => (
          photos.length ?
            photos.map((image, idx) => <ImageComponent
                key={idx}
                image={image}
                handlePhotoOnClick={handlePhotoOnClick}
                className={`imageContainer ${image.class}`}
                photoId={idx}
                {...provided.droppableProps}
                innerRef={provided.innerRef}
              >
                {provided.placeholder}
              </ImageComponent>):
            noImagesDiv

        )}
        </Droppable>
    </div>
  )
}

export default Dashboard;
