import React from 'react';

const PopUp = ({photoId, photos}) => {
  let image = photos.filter( photo => {
    return photo.id === photoId
  });
  const getImage = <img src={image[0].url} />;



  return (
    <div>
      <div className="popup-overlay">
      </div>
      <div className="full-image-component">
        <div className="close">X</div>
        <div className="image-fullsize-container">{getImage}</div>
        <div className="actions">
          <button className="crop">Crop</button>
          <button className="remove">Remove</button>
        </div>
      </div>
    </div>
  );
}

export default PopUp;
