import React from 'react';

const PopUp = ({photoId, photos, setPhotos, togglePopUp}) => {
  let image = photos.filter( photo => {
    return photo.id === photoId
  });
  const getImage = <img src={image[0].url} />;

  const handleRemove = () => {
    let newPhotos = photos.filter( photo => {
      return photo.id !== photoId
    });
    console.log(newPhotos);
    // setPhotos(newPhotos);
  }


  return (
    <div>
      <div className="popup-overlay" onClick={()=> togglePopUp('')}>
      </div>
      <div className="full-image-component">
        <div className="close-btn" onClick={()=> togglePopUp('')}>x</div>
        <div className="image-fullsize-container">{getImage}</div>
        <div className="image-actions">
          <button className="btn primary-btn crop-btn">Crop</button>
          <button className="danger-btn remove btn" onClick={handleRemove}>Remove</button>
        </div>
      </div>
    </div>
  );
}

export default PopUp;
