import React from 'react';

const Sidebar = ({imageUploadHandler, handleImageUpload, handleMosaicView}) => {

  return (
    <div className="sidebar-wrapper">
      <label className="btn primary-btn input-file-button">
        <input type="file" onChange={handleImageUpload}/>
        Upload Photo
      </label>
      <button className="btn primary-btn" onClick={handleMosaicView}>
        Mosaic View
      </button>
    </div>
  )
}

export default Sidebar;
