import React, {useState} from 'react';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/lib/ReactCrop.scss';

const PopUp = ({photoId, photos, setPhotos, togglePopUp}) => {
  let image = photos.filter( photo => photo.id === photoId );
  let imgSrc = image[0].url;
  const getImage = <img src={image[0].url} alt={photoId} />;

  const [crop, setCrop] = useState({
    src: image[0].url,
    crop: {
      unit: "%",
      x: 130,
      y: 50,
      width: 200,
      height: 200
    }
  });


  const handleRemove = () => {
    let newPhotos = photos.filter( photo => photo.id !== photoId );
    setPhotos(newPhotos);
    togglePopUp('');
  }

  const handleOnCropChange = (crop) => {
  //  setCrop({crop});
    console.log(crop)
  };

  const handleOnCropComplete = (crop) => {
    makeClientCrop(crop)
  };

  const makeClientCrop = async crop => {
    if(image && crop.width && crop.height) {
      const croppedImageUrl = await this.getCroppedImg(
        image,
        crop,
        "newFile.jpeg"
      );
      this.setState({ croppedImageUrl });
    }
  };

  const getCroppedImg = (image, crop, fileName) => {
    const canvas = document.createElement("canvas");
    const scaleX = image.naturalWidth / image.width;
    const scaleY = image.naturalHeight / image.height;
    canvas.width = crop.width;
    canvas.height = crop.height;
    const ctx = canvas.getContext("2d");

    ctx.drawImage(
      image,
      crop.x * scaleX,
      crop.y * scaleY,
      crop.width * scaleX,
      crop.height * scaleY,
      0,
      0,
      crop.width,
      crop.height
    );

    return new Promise((resolve, reject) => {
      canvas.toBlob(blob => {
        if (!blob) {
          //reject(new Error('Canvas is empty'));
          console.error("Canvas is empty");
          return;
        }
        blob.name = fileName;
        window.URL.revokeObjectURL(this.fileUrl);
        this.fileUrl = window.URL.createObjectURL(blob);
        resolve(this.fileUrl);
      }, "image/jpeg");
    });
  };

  return (
    <div>
      <div className="popup-overlay" onClick={()=> togglePopUp('')}>
      </div>
      <div className="full-image-component">
        <div className="close-btn" onClick={()=> togglePopUp('')}>x</div>
        {/* <div className="image-fullsize-container">{getImage}</div> */}
        <ReactCrop
          src={crop.src}
          alt={photoId}
          crop={crop}
          onChange={handleOnCropChange}
          onCropComplete={handleOnCropComplete}
        />
        <div className="image-actions">
          <button className="btn primary-btn crop-btn">Crop</button>
          <button className="danger-btn remove btn" onClick={handleRemove}>Remove</button>
        </div>
      </div>
    </div>
  );
}

export default PopUp;
