import React from 'react';
import ImageComponent from './components/ImageComponent';

const Dashboard = ({photos, handlePhotoOnClick}) => {
  let noImagesDiv = <div className="no-photos"> Step up your photography game. Upload your first photo :) </div>;
  // const drag = (e) => {
  //   e.preventDefault();
  //   e.dataTransfer.setData('image', e.currentTarget.id);
  //   console.log('dragging', e.currentTarget.id);
  // };
  //
  // const drop = (e) => {
  //   e.preventDefault();
  //   let data = e.dataTransfer.getData('image');
  //   console.log('data',data);
  //   e.target.appendChild(document.getElementById(data));
  // }
  //
  // const allowDrop = (e) => {
  //   e.preventDefault();
  // };
  return (
    <div className="dashboard-wrapper" id="dashboard">
      {photos.length ?
        photos.map((image, idx) => <ImageComponent
            key={idx}
            image={image}
            handlePhotoOnClick={handlePhotoOnClick}
            className={`imageContainer ${image.class}`}
            photoId={idx}
          />):
        noImagesDiv }
    </div>
  )
}

export default Dashboard;
