import React from 'react';

const PopUp = ({photoId, photos}) => {

  let image = photos.filter( photo => {
    return photo.id === photoId
  });

  let imageObject = getImage(image);

  const getImage = (image) => {
    let img = new Image();
    img.src = image.url;
    img.onload = () => {
      return img;
    }
  }

  return (
    <div>
      <div className="popup-overlay">
      </div>
      <div className="new-card-component"> {imageObject} </div>
    </div>
  );
}

export default PopUp;
