import React, {useState, useEffect} from 'react';
import SideBar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import NavBar from './components/NavBar';

const PhotoManager = () => {

  let myLocalStorageItems = JSON.parse(localStorage.getItem("myPhotosLocalStorage"));

  const [photos, setPhotos] = useState(myLocalStorageItems || []);

  useEffect(() => {
    localStorage.setItem('myPhotosLocalStorage', JSON.stringify(photos));
  }, [photos]);

  const handleImageSelected = (e) => {
    let photosClone = [...photos];
    setPhotos(photosClone, e.target.files[0]);
  }

  const handleImageUpload = (theEvt) => {

    var thefile = theEvt.target.files[0];
    if (!thefile.type.match("image.*")) {
        return;
    }
    var reader = new FileReader();
    reader.onload = function (evt) {
        var resultdata = evt.target.result;

        var img = new Image();
        let imgSrc = evt.target.result
        img.src = evt.target.result;
        img.onload = () => {
          let imgWidth = img.naturalWidth;
          let imgHeight = img.naturalHeight;
          let imageClass = '';

          if(imgWidth > imgHeight && (imgWidth - imgHeight) > 200) {
            imageClass = 'horizontal'
          } else if(imgWidth < imgHeight) {
            imageClass = 'vertical';
          } else if(imgWidth > 800 && imgHeight > 700) {
            imageClass = 'big'
          }
          let finalImgObj = {
            url : imgSrc,
            class : imageClass
          };
          const photosPile = [...photos, finalImgObj];
          setPhotos(photosPile);
        }

    }

    reader.readAsDataURL(thefile);
    // const photosPile = [...photos, e.target.files[0]];
    // setPhotos(photosPile);
    // console.log('p', photos)
  }

  return (
    <div className="photo-manager-wrapper">
      <NavBar />
      <div className="photo-manager-dashboard">
        <SideBar setPhotos={setPhotos} handleImageUpload={handleImageUpload} />
        <Dashboard photos={photos} />
      </div>
    </div>
  );
}

export default PhotoManager;
