import React from 'react';

const Sidebar = () => {
  const {imageUploadHandler, handleImageUpload, handleMosaicView, handleSortPhotos} = props;
  return (
    <div className="sidebar-wrapper">
      <label className="btn primary-btn input-file-button">
        <input type="file" onChange={handleImageUpload}/>
        Upload Photo
      </label>
      <button id="mosaic-btn" className="btn secondary-btn mosaic-view-btn" onClick={handleMosaicView}>
        Mosaic View
      </button>
      <button id="sort-btn" className="btn" onClick={handleSortPhotos}>
        Sort Images
      </button>
    </div>
  )
}

export default Sidebar;
